import { useState } from "react";
import "./App.css";

function App() {
  const [bookTitle, setBookTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [genre, setGenre] = useState("");
  const [rating, setRating] = useState("");
  const [reviewText, setReviewText] = useState("");

  const submitReview = async () => {
    const review = {
      bookTitle,
      author,
      genre,
      rating,
      reviewText
    };

    await fetch("http://localhost:8080/api/reviews", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(review)
    });

    alert("Review submitted!");
    setBookTitle("");
    setAuthor("");
    setGenre("");
    setRating("");
    setReviewText("");
  };

  return (
    <div className="container">
      <h1>ðŸ“š BookNest Reviews</h1>

      <input placeholder="Book Title" value={bookTitle}
        onChange={(e) => setBookTitle(e.target.value)} />

      <input placeholder="Author" value={author}
        onChange={(e) => setAuthor(e.target.value)} />

      <input placeholder="Genre" value={genre}
        onChange={(e) => setGenre(e.target.value)} />

      <input type="number" placeholder="Rating (1-5)" value={rating}
        onChange={(e) => setRating(e.target.value)} />

      <textarea placeholder="Your Review" value={reviewText}
        onChange={(e) => setReviewText(e.target.value)} />

      <button onClick={submitReview}>Submit Review</button>
    </div>
  );
}

export default App;
