import React, { useState } from "react";
import "./App.css";

function App() {
  const [page, setPage] = useState("create");
  const [books, setBooks] = useState([]);
  const [editId, setEditId] = useState(null);

  const [formData, setFormData] = useState({
    title: "",
    author: "",
    genre: "",
    review: "",
    rating: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const submitReview = () => {
    if (!formData.title || !formData.author) return;

    if (editId) {
      setBooks(
        books.map((b) =>
          b.id === editId ? { ...formData, id: editId } : b
        )
      );
      setEditId(null);
    } else {
      setBooks([...books, { ...formData, id: Date.now() }]);
    }

    setFormData({ title: "", author: "", genre: "", review: "", rating: "" });
    setPage("view");
  };

  const editBook = (book) => {
    setFormData(book);
    setEditId(book.id);
    setPage("create");
  };

  const deleteBook = (id) => {
    setBooks(books.filter((b) => b.id !== id));
  };

  return (
    <div className="container">
      <h1>üìö BookNest</h1>

      <div className="nav">
        <button onClick={() => setPage("create")}>Create Review</button>
        <button onClick={() => setPage("view")}>View Reviews</button>
      </div>

      {page === "create" && (
        <div className="form">
          <h2>{editId ? "Edit Review" : "Create Review"}</h2>

          <input name="title" placeholder="Book Title" value={formData.title} onChange={handleChange} />
          <input name="author" placeholder="Author" value={formData.author} onChange={handleChange} />

          <select name="genre" value={formData.genre} onChange={handleChange}>
            <option value="">Select Genre</option>
            <option>Comics</option>
            <option>Romance</option>
            <option>Fantasy</option>
            <option>Thriller</option>
            <option>Self-Help</option>
          </select>

          <input name="rating" placeholder="Rating (1-5)" value={formData.rating} onChange={handleChange} />
          <textarea name="review" placeholder="Write review..." value={formData.review} onChange={handleChange}></textarea>

          <button onClick={submitReview}>
            {editId ? "Update Review" : "Add Review"}
          </button>
        </div>
      )}

      {page === "view" && (
        <div className="list">
          {books.length === 0 && <p className="empty">No reviews available</p>}

          {books.map((book) => (
            <div className="card" key={book.id}>
              <h3>{book.title}</h3>
              <p><b>Author:</b> {book.author}</p>
              <p><b>Genre:</b> {book.genre}</p>
              <p><b>Rating:</b> ‚≠ê {book.rating}</p>
              <p className="review">{book.review}</p>

              <div className="actions">
                <button className="edit" onClick={() => editBook(book)}>Edit</button>
                <button className="delete" onClick={() => deleteBook(book.id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
