import React, { useState } from "react";
import "./App.css";

function App() {
  const [books, setBooks] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    author: "",
    genre: "",
    review: "",
    rating: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const addBook = () => {
    if (!formData.title || !formData.author) return;
    setBooks([...books, { ...formData, id: Date.now() }]);
    setFormData({ title: "", author: "", genre: "", review: "", rating: "" });
  };

  const deleteBook = (id) => {
    setBooks(books.filter((book) => book.id !== id));
  };

  return (
    <div className="container">
      <h1>üìö BookNest</h1>
      <p className="subtitle">Book Review Management System</p>

      <div className="form">
        <input name="title" placeholder="Book Title" value={formData.title} onChange={handleChange} />
        <input name="author" placeholder="Author" value={formData.author} onChange={handleChange} />

        <select name="genre" value={formData.genre} onChange={handleChange}>
          <option value="">Select Genre</option>
          <option>Comics</option>
          <option>Romance</option>
          <option>Fantasy</option>
          <option>Thriller</option>
          <option>Self-Help</option>
        </select>

        <input name="rating" placeholder="Rating (1-5)" value={formData.rating} onChange={handleChange} />
        <textarea name="review" placeholder="Write your review..." value={formData.review} onChange={handleChange}></textarea>

        <button onClick={addBook}>Add Review</button>
      </div>

      <div className="list">
        {books.map((book) => (
          <div className="card" key={book.id}>
            <h3>{book.title}</h3>
            <p><b>Author:</b> {book.author}</p>
            <p><b>Genre:</b> {book.genre}</p>
            <p><b>Rating:</b> ‚≠ê {book.rating}</p>
            <p className="review">{book.review}</p>
            <button className="delete" onClick={() => deleteBook(book.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
