import React, { useEffect, useState } from "react";
import "./App.css";

const API_BASE_URL = "http://localhost:8080/api"; // adjust to your backend

function App() {
  const [books, setBooks] = useState([]);
  const [selectedBookId, setSelectedBookId] = useState(null);
  const [bookForm, setBookForm] = useState({
    title: "",
    author: "",
    genre: "",
    year: "",
    description: "",
  });
  const [reviewForm, setReviewForm] = useState({
    reviewerName: "",
    rating: "",
    comment: "",
  });

  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("books"); // "books" | "reviews"

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      setLoading(true);
      const res = await fetch(`${API_BASE_URL}/books`);
      const data = await res.json();
      setBooks(data);
    } catch (err) {
      console.error("Failed to fetch books", err);
    } finally {
      setLoading(false);
    }
  };

  const resetBookForm = () => {
    setBookForm({
      title: "",
      author: "",
      genre: "",
      year: "",
      description: "",
    });
    setSelectedBookId(null);
  };

  const handleBookChange = (e) => {
    const { name, value } = e.target;
    setBookForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleReviewChange = (e) => {
    const { name, value } = e.target;
    setReviewForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleBookSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      ...bookForm,
      year: bookForm.year ? Number(bookForm.year) : null,
    };

    try {
      setLoading(true);
      const res = await fetch(
        `${API_BASE_URL}/books${selectedBookId ? `/${selectedBookId}` : ""}`,
        {
          method: selectedBookId ? "PUT" : "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        }
      );
      if (!res.ok) throw new Error("Failed to save book");
      await fetchBooks();
      resetBookForm();
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleEditBook = (book) => {
    setSelectedBookId(book.id);
    setBookForm({
      title: book.title || "",
      author: book.author || "",
      genre: book.genre || "",
      year: book.year || "",
      description: book.description || "",
    });
    setActiveTab("books");
  };

  const handleDeleteBook = async (id) => {
    if (!window.confirm("Delete this book?")) return;
    try {
      setLoading(true);
      await fetch(`${API_BASE_URL}/books/${id}`, {
        method: "DELETE",
      });
      await fetchBooks();
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleReviewSubmit = async (e, bookId) => {
    e.preventDefault();
    const payload = {
      ...reviewForm,
      rating: reviewForm.rating ? Number(reviewForm.rating) : null,
    };

    try {
      setLoading(true);
      const res = await fetch(`${API_BASE_URL}/books/${bookId}/reviews`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      if (!res.ok) throw new Error("Failed to add review");
      await fetchBooks();
      setReviewForm({
        reviewerName: "",
        rating: "",
        comment: "",
      });
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const averageRating = (reviews = []) => {
    if (!reviews.length) return "No ratings yet";
    const sum = reviews.reduce((acc, r) => acc + (r.rating || 0), 0);
    return `${(sum / reviews.length).toFixed(1)} ★`;
  };

  return (
    <div className="app-root">
      <header className="app-header">
        <div className="app-logo">BookVerse</div>
        <nav className="app-nav">
          <button
            className={`nav-tab ${activeTab === "books" ? "nav-tab--active" : ""}`}
            onClick={() => setActiveTab("books")}
          >
            Books
          </button>
          <button
            className={`nav-tab ${
              activeTab === "reviews" ? "nav-tab--active" : ""
            }`}
            onClick={() => setActiveTab("reviews")}
          >
            Reviews
          </button>
        </nav>
      </header>

      <main className="app-main">
        <section className="panel panel--left">
          <h2 className="panel-title">
            {selectedBookId ? "Edit Book" : "Add a Book"}
          </h2>
          <form className="form" onSubmit={handleBookSubmit}>
            <div className="form-group">
              <label>Title</label>
              <input
                name="title"
                value={bookForm.title}
                onChange={handleBookChange}
                placeholder="Eg: Atomic Habits"
                required
              />
            </div>
            <div className="form-group">
              <label>Author</label>
              <input
                name="author"
                value={bookForm.author}
                onChange={handleBookChange}
                placeholder="James Clear"
                required
              />
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>Genre</label>
                <input
                  name="genre"
                  value={bookForm.genre}
                  onChange={handleBookChange}
                  placeholder="Self-help, Fiction..."
                />
              </div>
              <div className="form-group">
                <label>Year</label>
                <input
                  type="number"
                  name="year"
                  value={bookForm.year}
                  onChange={handleBookChange}
                  placeholder="2018"
                  min="0"
                />
              </div>
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                value={bookForm.description}
                onChange={handleBookChange}
                placeholder="Short description of the book..."
                rows={3}
              />
            </div>

            <div className="form-actions">
              <button
                type="submit"
                className="btn btn--primary"
                disabled={loading}
              >
                {selectedBookId ? "Update Book" : "Create Book"}
              </button>
              {selectedBookId && (
                <button
                  type="button"
                  className="btn btn--ghost"
                  onClick={resetBookForm}
                >
                  Cancel
                </button>
              )}
            </div>
          </form>
        </section>

        <section className="panel panel--right">
          <div className="panel-header">
            <h2 className="panel-title">Library</h2>
            {loading && <span className="badge badge--muted">Syncing…</span>}
          </div>

          {books.length === 0 && !loading && (
            <p className="empty-state">No books yet. Start by adding one.</p>
          )}

          <div className="book-grid">
            {books.map((book) => (
              <article key={book.id} className="book-card">
                <div className="book-header">
                  <div>
                    <h3 className="book-title">{book.title}</h3>
                    <p className="book-author">by {book.author}</p>
                  </div>
                  <span className="book-rating">
                    {averageRating(book.reviews)}
                  </span>
                </div>

                <p className="book-meta">
                  {book.genre && <span>{book.genre}</span>}
                  {book.year && <span>• {book.year}</span>}
                </p>

                {book.description && (
                  <p className="book-description">{book.description}</p>
                )}

                <div className="book-actions">
                  <button
                    className="btn btn--small"
                    onClick={() => handleEditBook(book)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn--small btn--danger"
                    onClick={() => handleDeleteBook(book.id)}
                  >
                    Delete
                  </button>
                </div>

                {activeTab === "reviews" && (
                  <div className="reviews-section">
                    <h4 className="reviews-title">Reviews</h4>
                    <ul className="review-list">
                      {(book.reviews || []).map((rev) => (
                        <li key={rev.id} className="review-item">
                          <div className="review-header">
                            <span className="reviewer">
                              {rev.reviewerName || "Anonymous"}
                            </span>
                            <span className="review-rating">
                              {rev.rating} ★
                            </span>
                          </div>
                          <p className="review-comment">{rev.comment}</p>
                        </li>
                      ))}
                      {(book.reviews || []).length === 0 && (
                        <li className="review-empty">
                          No reviews yet. Be the first to add one.
                        </li>
                      )}
                    </ul>

                    <form
                      className="form form--inline"
                      onSubmit={(e) => handleReviewSubmit(e, book.id)}
                    >
                      <div className="form-row">
                        <input
                          name="reviewerName"
                          value={reviewForm.reviewerName}
                          onChange={handleReviewChange}
                          placeholder="Your name"
                        />
                        <input
                          type="number"
                          name="rating"
                          value={reviewForm.rating}
                          onChange={handleReviewChange}
                          placeholder="Rating"
                          min="1"
                          max="5"
                          required
                        />
                      </div>
                      <div className="form-group">
                        <textarea
                          name="comment"
                          value={reviewForm.comment}
                          onChange={handleReviewChange}
                          placeholder="Write a short review..."
                          rows={2}
                          required
                        />
                      </div>
                      <button
                        type="submit"
                        className="btn btn--primary btn--small"
                        disabled={loading}
                      >
                        Add Review
                      </button>
                    </form>
                  </div>
                )}
              </article>
            ))}
          </div>
        </section>
      </main>

      <footer className="app-footer">
        <span>BookVerse Reviews · Built with React</span>
      </footer>
    </div>
  );
}

export default App;
